import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddBookForm = () => {
  const [title, setTitle] = useState('');
  const [authorId, setAuthorId] = useState('');
  const [genreId, setGenreId] = useState('');
  const [price, setPrice] = useState('');
  const [publicationDate, setPublicationDate] = useState('');
  const [images, setImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    // Simulate fetching images from an API endpoint
    const fetchImages = async () => {
      try {
        // Replace with actual API endpoint to fetch images
        const response = await axios.get('http://example.com/api/images');
        setImages(response.data); // Assuming response.data is an array of image URLs
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };

    fetchImages();
  }, []);

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/books', {
        title,
        author_id: parseInt(authorId),  // Ensure authorId is parsed as integer
        genre_id: parseInt(genreId),    // Ensure genreId is parsed as integer
        price,
        publication_date: publicationDate,
        imageUrl: images[currentImageIndex] // Include current image URL in the form data
      });
      console.log('Book added:', response.data);
      // Clear form fields or redirect to books page
      setTitle('');
      setAuthorId('');
      setGenreId('');
      setPrice('');
      setPublicationDate('');
    } catch (error) {
      console.error('Error adding book:', error);
    } 
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Title:</label>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />

        <label>Author ID:</label>
        <input type="text" value={authorId} onChange={(e) => setAuthorId(e.target.value)} required />

        <label>Genre ID:</label>
        <input type="text" value={genreId} onChange={(e) => setGenreId(e.target.value)} required />

        <label>Price:</label>
        <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} required />

        <label>Publication Date:</label>
        <input type="text" value={publicationDate} onChange={(e) => setPublicationDate(e.target.value)} required />

        {/* Display the current selected image */}
        {images.length > 0 && (
          <div className="mb-3">
            <label>Choose Image:</label>
            <div className="d-flex align-items-center">
              <img src={images[currentImageIndex]} alt="Book Image" className="img-fluid me-3" style={{ maxHeight: '100px' }} />
              <select
                className="form-select"
                value={currentImageIndex}
                onChange={(e) => setCurrentImageIndex(parseInt(e.target.value))}
              >
                {images.map((image, index) => (
                  <option key={index} value={index}>{`Image ${index + 1}`}</option>
                ))}
              </select>
            </div>
          </div>
        )}

        <button type="submit" className="btn btn-primary">Add Book</button>
      </form>
    </div>
  );
};

export default AddBookForm;
