import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { BsPlusSquare, BsPencilSquare, BsSearch, BsTrash, BsListUl } from 'react-icons/bs';

const AuthorsPage = () => {
  const [authors, setAuthors] = useState([]);

  useEffect(() => {
    fetchAuthors();
  }, []);

  const fetchAuthors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/authors');
      setAuthors(response.data);
    } catch (error) {
      console.error('Error fetching authors:', error);
    }
  };

  const handleDeleteAuthor = async (authorId) => {
    if (window.confirm('Are you sure you want to delete this author?')) {
      try {
        await axios.delete(`http://localhost:5000/authors/${authorId}`);
        // After successful deletion, update the authors list
        setAuthors(authors.filter(author => author.author_id !== authorId));
      } catch (error) {
        console.error('Error deleting author:', error);
      }
    }
  };

  return (
    <div className="row">
      {/* Left side content */}
      <div className="col-lg-8">
        <h2>Admin Library - All Authors</h2>
        <br></br>
        <br></br>
        <div className="mb-3">
          {/* Row 1: Add, Edit, Delete */}
          <div className="btn-group mb-3" role="group">
            <Link to="/authors/add" className="btn btn-primary me-2">
              <BsPlusSquare className="me-2" /> Add Author
            </Link>
            <Link to="/authors/edit" className="btn btn-warning me-2">
              <BsPencilSquare className="me-2" /> Edit Author
            </Link>
            <Link to="/" className="btn btn-info me-2">
              <BsSearch className="me-2" /> Search Author
            </Link>
          </div>

          {/* Row 2: View, Delete */}
          <div className="btn-group mb-3" role="group">
            <Link to="/authors/viewauthor" className="btn btn-dark me-2">
              <BsListUl className="me-2" /> View Authors
            </Link>
          </div>
        </div>
        <ul className="list-group">
          {authors.map(author => (
            <li key={author.author_id} className="list-group-item d-flex justify-content-between align-items-center">
              <Link to={`/authors/${author.author_id}`}>{author.name}</Link>
              <div>
                <Link to={`/authors/edit/${author.author_id}`} className="btn btn-sm btn-outline-primary me-2">
                  Edit
                </Link>
                <button
                  className="btn btn-sm btn-outline-danger"
                  onClick={() => handleDeleteAuthor(author.author_id)}
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {/* Right side content (optional) */}
      <div className="col-lg-4">
        {/* Add any additional content for the right side here */}
      </div>
    </div>
  );
};

export default AuthorsPage;
