import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import UserProfile from './UserProfile';
import SearchBooks from './SearchBooks'; 
import IssuedBooks from './IssuedBooks'; 
import Wishlist from './Wishlist'; 
import FooterInside from './Footer_inside';

const DashboardUser = () => {
  const [selectedNavItem, setSelectedNavItem] = useState('profile'); 

  const renderSelectedContent = () => {
    switch (selectedNavItem) {
      case 'profile':
        return <UserProfile />;
      case 'search_books':
        return <SearchBooks />;
      case 'issued_books':
        return <IssuedBooks />;
      case 'wishlist':
        return <Wishlist />;
      default:
        return null;
    }
  };

  return (
    <div className="container-fluid">
      <nav className="navbar navbar-expand-lg navbar-dark bg-info">
        <div className="container-fluid">
          <Link className="navbar-brand d-flex align-items-center" to="/">
            <img
              src={process.env.PUBLIC_URL + '/images/booklogo.png'}
              alt="BookLogo"
              width="70"
              height="70"
              className="d-inline-block align-top me-2"
            />
            Book Hub
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="navbar-text ms-auto">Hello, User</div> {/* Change to user-specific greeting */}
        </div>
      </nav>

      <div className="row">
        {/* Sidebar */}
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav flex-column">
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'profile' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('profile')}
                >
                  Profile
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'search_books' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('search_books')}
                >
                  Search Books
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'issued_books' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('issued_books')}
                >
                  Issued Books
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'wishlist' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('wishlist')}
                >
                  Wishlist
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        {/* Page content */}
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="pt-3 pb-2 mb-3">
            {renderSelectedContent()}
          </div>
        </main>
      </div>

      <FooterInside />
    </div>
  );
};

export default DashboardUser;
