import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const EditBookId = () => {
  const { book_id } = useParams();

  const [bookDetails, setBookDetails] = useState(null);
  const [title, setTitle] = useState('');
  const [authorName, setAuthorName] = useState('');
  const [genreName, setGenreName] = useState('');
  const [price, setPrice] = useState('');
  const [publicationDate, setPublicationDate] = useState('');

  useEffect(() => {
    if (book_id) {
      fetchBookDetails();
    }
  }, [book_id]);

  const fetchBookDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/books/${book_id}`);
      console.log('Book details fetched:', response.data);
      const { title, author_name, GenreName, price, publication_date } = response.data;
      setBookDetails(response.data);
      setTitle(title);
      setAuthorName(author_name);
      setGenreName(GenreName); // Use GenreName from response data
      setPrice(price);
      setPublicationDate(new Date(publication_date).toISOString().slice(0, 10));
    } catch (error) {
      console.error('Error fetching book details:', error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5000/books/${book_id}`, {
        title,
        author_name: authorName,
        genre_name: genreName,
        price,
        publication_date: new Date(publicationDate).toISOString().slice(0, 10),
      });
      console.log('Book updated:', response.data);
      alert('Book updated successfully!');
    } catch (error) {
      console.error('Error updating book:', error);
    }
  };

  return (
    <div className="container">
      {bookDetails && (
        <div className="card shadow mb-4">
          <div className="card-body">
            <h5 className="card-title text-center mb-4">Book Details (ID: {book_id})</h5>
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Author Name</th>
                  <th>Genre Name</th>
                  <th>Price</th>
                  <th>Publication Date</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{bookDetails.title}</td>
                  <td>{bookDetails.author_name}</td>
                  <td>{bookDetails.GenreName}</td>
                  <td>{bookDetails.price}</td>
                  <td>{new Date(bookDetails.publication_date).toLocaleDateString()}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      )}

      <div className="card shadow">
        <div className="card-body">
          <h5 className="card-title text-center mb-4">Edit Book</h5>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">Title:</label>
              <input type="text" id="title" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>

            <div className="mb-3">
              <label htmlFor="authorName" className="form-label">Author Name:</label>
              <input type="text" id="authorName" className="form-control" value={authorName} onChange={(e) => setAuthorName(e.target.value)} required />
            </div>

            <div className="mb-3">
              <label htmlFor="genreName" className="form-label">Genre Name:</label>
              <input type="text" id="genreName" className="form-control" value={genreName} onChange={(e) => setGenreName(e.target.value)} required />
            </div>

            <div className="mb-3">
              <label htmlFor="price" className="form-label">Price:</label>
              <input type="text" id="price" className="form-control" value={price} onChange={(e) => setPrice(e.target.value)} required />
            </div>

            <div className="mb-3">
              <label htmlFor="publicationDate" className="form-label">Publication Date:</label>
              <input type="date" id="publicationDate" className="form-control" value={publicationDate} onChange={(e) => setPublicationDate(e.target.value)} required />
            </div>

            <div className="text-center">
              <button type="submit" className="btn btn-primary">Update Book</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditBookId;
