import React, { useState } from 'react';
import axios from 'axios';
import FormData from 'form-data';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const AddBookForm = () => {
  const [title, setTitle] = useState('');
  const [authorName, setAuthorName] = useState('');
  const [genreName, setGenreName] = useState('');
  const [price, setPrice] = useState('');
  const [publicationDate, setPublicationDate] = useState(new Date());
  const [image, setImage] = useState(null); // State for storing selected file

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Create form data object and append fields
      const formData = new FormData();
      formData.append('title', title);
      formData.append('author_name', authorName);
      formData.append('genre_name', genreName);
      formData.append('price', price);
      formData.append('publication_date', publicationDate.toISOString().split('T')[0]);
      formData.append('image', image);

      // Make POST request using axios with FormData
      const response = await axios.post('http://localhost:5000/books', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('Book added:', response.data);
      // Reset form fields
      setTitle('');
      setAuthorName('');
      setGenreName('');
      setPrice('');
      setPublicationDate(new Date());
      setImage(null);

    } catch (error) {
      console.error('Error adding book:', error);
    }
  };

  return (
    <div className="container">
      <div className="card shadow">
        <div className="card-body">
          <h5 className="card-title text-center mb-4">Add New Book</h5>
          <form onSubmit={handleSubmit}>
            {/* Existing form fields */}
            {/* Add input for image */}
            <div className="mb-3">
              <label htmlFor="image" className="form-label">Book Image:</label>
              <input
                type="file"
                id="image"
                className="form-control"
                onChange={(e) => setImage(e.target.files[0])}
                required
              />
            </div>

            <div className="text-center">
              <button type="submit" className="btn btn-primary">Add Book</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddBookForm;
