import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';
import { BsPlusSquare, BsPencilSquare, BsSearch, BsTrash, BsListUl, BsArrowLeft, BsArrowRight } from 'react-icons/bs';
import bookImage from './images/adminimage.png'; // Import your book image

const BooksPage = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const history = useHistory();

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const handleDeleteBook = async (bookId) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      try {
        await axios.delete(`http://localhost:5000/books/${bookId}`);
        // Update the books state after deletion
        setBooks(books.filter(book => book.book_id !== bookId));
      } catch (error) {
        console.error('Error deleting book:', error);
      }
    }
  };

  const handleSearch = () => {
    // Perform search logic here (you can filter books array based on searchTerm)
    // For simplicity, let's assume searchTerm filters by book title
    const filteredBooks = books.filter(book => book.title.toLowerCase().includes(searchTerm.toLowerCase()));
    if (filteredBooks.length > 0) {
      // Navigate to the first matching book's edit form
      history.push(`/books/${filteredBooks[0].book_id}/edit`);
    } else {
      alert('No books found matching the search term.');
    }
  };

  return (
    <div className="row">
      <div className="col-lg-7">
        <h2>Admin Library - All Books</h2>
        <br></br>
        <br></br>
        <div className="mb-3">
          <div className="btn-group mb-3" role="group">
            <Link to="/books/add" className="btn btn-primary me-2">
              <BsPlusSquare className="me-2" /> Add Book
            </Link>
            <button className="btn btn-warning me-2">
              <BsPencilSquare className="me-2" /> Edit Book
            </button>
            <button className="btn btn-danger">
              <BsTrash className="me-2" /> Delete Book
            </button>
          </div>

          <div className="btn-group mb-3" role="group">
            <Link to="/" className="btn btn-secondary me-2">
              <BsArrowLeft className="me-2" /> Return Book
            </Link>
            <Link to="/" className="btn btn-secondary">
              <BsArrowRight className="me-2" /> Issue Book
            </Link>
          </div>
          <br></br>
          <br></br>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by title"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button className="btn btn-outline-secondary" type="button" onClick={handleSearch}>
              <BsSearch />
            </button>
          </div>
        </div>

        <ul className="list-group">
          {books.map(book => (
            <li key={book.book_id} className="list-group-item d-flex justify-content-between align-items-center">
              <Link to={`/books/${book.book_id}`}>{book.title}</Link>
              <div>
                <Link to={`/books/${book.book_id}/edit`} className="btn btn-sm btn-outline-primary me-2">
                  Edit
                </Link>
                <button onClick={() => handleDeleteBook(book.book_id)} className="btn btn-sm btn-outline-danger">
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="col-lg-5">
        <img src={bookImage} alt="Book Image" className="img-fluid mb-3" style={{ maxHeight: '700px' }} />
      </div>
    </div>
  );
};

export default BooksPage;
