// CarouselComponent.js
import React, { useState, useEffect } from 'react';

const CarouselComponent = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const images = [
    cd_image1,
    cd_image2,
    cd_image3,
    cd_image4,
    cd_image5
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex(prevIndex =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000); // Change image every 3 seconds (3000 milliseconds)

    return () => clearInterval(interval);
  }, [images.length]); // Ensure useEffect depends on images.length

  return (
    <div id="carouselExampleSlidesOnly" className="carousel slide" data-ride="carousel">
      <div className="carousel-inner">
        {images.map((image, index) => (
          <div key={index} className={`carousel-item ${index === currentImageIndex ? 'active' : ''}`}>
            <img src={image} className="d-block w-100" alt={`Slide ${index}`} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default CarouselComponent;
