import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const EditAuthorForm = () => {
  const { author_id } = useParams();
  const [authors, setAuthors] = useState([]);
  const [selectedAuthorId, setSelectedAuthorId] = useState('');
  const [authorDetails, setAuthorDetails] = useState(null);
  const [name, setName] = useState('');
  const [biography, setBiography] = useState('');
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchAuthors();
  }, []);

  const fetchAuthors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/authors');
      setAuthors(response.data);
    } catch (error) {
      console.error('Error fetching authors:', error);
    }
  };

  const handleAuthorChange = async (event) => {
    const selectedId = event.target.value;
    setSelectedAuthorId(selectedId);
    setLoading(true);

    try {
      const response = await axios.get(`http://localhost:5000/authors/${selectedId}`);
      const { name, biography } = response.data;
      setAuthorDetails(response.data);
      setName(name);
      setBiography(biography);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching author details:', error);
      setLoading(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5000/authors/${selectedAuthorId}`, {
        name,
        biography
      });
      console.log('Author updated:', response.data);
      // Show success message
      setSuccessMessage('Author updated successfully');
    } catch (error) {
      console.error('Error updating author:', error);
    }
  };

  return (
    <div>
      <h2>Edit Author</h2>
      <div>
        <label>Select Author:</label>
        <select value={selectedAuthorId} onChange={handleAuthorChange}>
          <option value="">Select an author</option>
          {authors.map((author) => (
            <option key={author.author_id} value={author.author_id}>
              {author.name}
            </option>
          ))}
        </select>
      </div>

      {loading ? (
        <p>Loading author details...</p>
      ) : authorDetails ? (
        <div>
          <form onSubmit={handleSubmit}>
            <label>Name:</label>
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />

            <label>Biography:</label>
            <textarea
              value={biography}
              onChange={(e) => setBiography(e.target.value)}
              required
            />

            <button type="submit">Update Author</button>
          </form>

          {successMessage && <p>{successMessage}</p>}

          <div>
            <h3>Author Details</h3>
            <p><strong>Name:</strong> {authorDetails.name}</p>
            <p><strong>Biography:</strong> {authorDetails.biography}</p>
          </div>
        </div>
      ) : (
        <p>No author selected</p>
      )}
    </div>
  );
};

export default EditAuthorForm;
