import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ManageBooks from './BooksPage'; // Import the ManageBooks component (to be created similarly for Manage Authors and Manage Genres)
import ManageAuthors from './AuthorsPage';
import Footer from './Footer'; // Import Footer component

const Dashboard = () => {
  const [selectedNavItem, setSelectedNavItem] = useState('dashboard'); // State to manage selected navigation item

  // Function to render selected content based on selectedNavItem state
  const renderSelectedContent = () => {
    switch (selectedNavItem) {
      case 'dashboard':
        return (
          <div>
            <h2>Dashboard</h2>
            <p>Welcome to the Admin Dashboard of BookHub.</p>
          </div>
        );
      case 'manage_books':
        return <ManageBooks />;
        case 'manage_authors':
            return <ManageAuthors />;

      // Add cases for manage_authors and manage_genres similarly
      default:
        return null;
    }
  };

  return (
    <div className="container-fluid">
        <nav className="navbar navbar-expand-lg navbar-dark bg-info">
      <div className="container-fluid">
        {/* Logo with BookHub name */}
        <Link className="navbar-brand d-flex align-items-center" to="/">
          <img src={process.env.PUBLIC_URL + '/images/booklogo.png'} alt="BookLogo" width="70" height="70" className="d-inline-block align-top me-2" />
          Book Hub
        </Link>

        {/* Navbar Toggler Button */}
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
      {/* Navbar */}
      {/*<nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container">
          <Link className="navbar-brand" to="/">
            BookHub
  </Link> */}
          <div className="navbar-text ms-auto">
            Hello, Admin
          </div>
        </div>
      </nav>

      {/* Main content */}
      <div className="row">
        {/* Sidebar */}
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav flex-column">
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'dashboard' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('dashboard')}
                >
                  Dashboard
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_books' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_books')}
                >
                  Manage Books
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_authors' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_authors')}
                >
                  Manage Authors
                </Link>
              </li>

              {/* Add similar links for Manage Authors and Manage Genres */}
            </ul>
          </div>
        </nav>

        {/* Page content */}
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="pt-3 pb-2 mb-3">
            {/* Content area */}
            {renderSelectedContent()}

          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
