import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { BsPlusSquare, BsPencilSquare, BsTrash, BsListUl } from 'react-icons/bs';
import authorImage from './images/authorimage.jpeg'; // Import author image

const AuthorsPage = () => {
  const [authors, setAuthors] = useState([]);

  useEffect(() => {
    fetchAuthors();
  }, []);

  const fetchAuthors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/authors');
      setAuthors(response.data);
    } catch (error) {
      console.error('Error fetching authors:', error);
    }
  };

  const handleDeleteAuthor = async (authorId) => {
    if (window.confirm('Are you sure you want to delete this author?')) {
      try {
        await axios.delete(`http://localhost:5000/authors/${authorId}`);
        setAuthors(authors.filter(author => author.author_id !== authorId));
      } catch (error) {
        console.error('Error deleting author:', error);
      }
    }
  };

  return (
    <div className="row">
      {/* Left side content */}
      <div className="col-lg-7">
        <div className="card mb-3">
          <div className="card-body">
            <h2 className="card-title">Manage Authors</h2>
            <p className="card-text">Manage your authors effectively with ease and efficiency!</p>

            {/* Action buttons */}
            <div className="d-flex justify-content-between align-items-center mb-3">
              <div className="btn-group">
                <Link to="/authors/add" className="btn btn-primary">
                  <BsPlusSquare className="me-2" /> Add Author
                </Link>
                <Link to="/authors/edit" className="btn btn-warning ms-2">
                  <BsPencilSquare className="me-2" /> Edit Author
                </Link>
                <Link to={`/authors/delete`} className="btn btn-danger ms-2">
                  <BsTrash className="me-2" /> Delete Author
                </Link>
              </div>
              <div>
                <Link to="/authors/viewauthor" className="btn btn-dark">
                  <BsListUl className="me-2" /> View Authors
                </Link>
              </div>
            </div>

            {/* List of authors */}
            <div className="row row-cols-1 row-cols-md-2 g-4">
              {authors.map(author => (
                <div key={author.author_id} className="col">
                  <div className="card h-100">
                    <img src={authorImage} className="card-img-top" alt="Author" />
                    <div className="card-body">
                      <h5 className="card-title">{author.author_name}</h5>
                      <p className="card-text">{author.bio}</p>
                      <button className="btn btn-danger" onClick={() => handleDeleteAuthor(author.author_id)}>
                        Delete
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Right side content */}
      <div className="col-lg-5">
        {/* Placeholder for additional content */}
      </div>
    </div>
  );
};

export default AuthorsPage;
