import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { BsArrowLeft } from 'react-icons/bs';
import axios from 'axios';

const Returnbook = () => {
  const [issuedBooks, setIssuedBooks] = useState([]);
  const [selectedIssuedBookId, setSelectedIssuedBookId] = useState('');
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchIssuedBooks();
  }, []);

  const fetchIssuedBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books/issued');
      setIssuedBooks(response.data);
    } catch (error) {
      console.error('Error fetching issued books:', error);
      setError('Error fetching issued books');
    }
  };

  const handleReturnBook = async () => {
    try {
      await axios.put(`http://localhost:5000/books/return/${selectedIssuedBookId}`);
      setSuccessMessage('Book returned successfully');
    } catch (error) {
      console.error('Error returning book:', error);
      setError('Error returning book');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Return Book</h2>
      <Link to="/books" className="btn btn-secondary me-2">
        <BsArrowLeft className="me-2" /> Back to Books
      </Link>
      {error && <div className="alert alert-danger mt-3">{error}</div>}
      {successMessage && <div className="alert alert-success mt-3">{successMessage}</div>}
      <div className="mt-3">
        <label htmlFor="issuedBookSelect" className="form-label">Select Issued Book:</label>
        <select id="issuedBookSelect" className="form-select" value={selectedIssuedBookId} onChange={(e) => setSelectedIssuedBookId(e.target.value)}>
          <option value="">Select an issued book</option>
          {issuedBooks.map(book => (
            <option key={book.book_id} value={book.book_id}>{book.title}</option>
          ))}
        </select>
      </div>
      <button className="btn btn-primary mt-3" onClick={handleReturnBook}>Return Book</button>
    </div>
  );
};

export default Returnbook;
