import React, { useState } from 'react';
import axios from 'axios';

const SearchBooks = () => {
  const [query, setQuery] = useState('');
  const [books, setBooks] = useState([]);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/books/search?query=${query}`);
      setBooks(response.data);
      setError('');
    } catch (err) {
      setError('Error fetching data');
      console.error(err);
    }
  };

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleSearch();
  };

  return (
    <div>
      <h2>Search Books</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="searchInput" className="form-label">Search by Title:</label>
          <input
            type="text"
            className="form-control"
            id="searchInput"
            value={query}
            onChange={handleChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">Search</button>
      </form>

      {error && <div className="alert alert-danger mt-3">{error}</div>}

      <div className="mt-4">
        {books.length > 0 ? (
          <div>
            <h3>Search Results:</h3>
            <ul className="list-group">
              {books.map((book) => (
                <li key={book.book_id} className="list-group-item">
                  <strong>Title:</strong> {book.title}<br />
                 // <strong>Author:</strong> {book.author_name}<br />
                  <strong>Genre:</strong> {book.genre_name}<br />
                  <strong>Price:</strong> ${book.price}<br />
                  <strong>Publication Date:</strong> {new Date(book.publication_date).toLocaleDateString()}
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <p>No books found</p>
        )}
      </div>
    </div>
  );
};

export default SearchBooks;
