import React, { useState } from 'react';
import axios from 'axios';

const AddAuthorForm = () => {
  const [name, setName] = useState('');
  const [biography, setBiography] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/authors', {
        name,
        biography
      });
      console.log('Author added:', response.data);
      setSuccessMessage('Author added successfully');
      setErrorMessage('');
      // Clear form fields
      setName('');
      setBiography('');
    } catch (error) {
      console.error('Error adding author:', error);
      setErrorMessage('Failed to add author');
      setSuccessMessage('');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Add New Author</h2>
      <form onSubmit={handleSubmit}>
        {successMessage && <div className="alert alert-success">{successMessage}</div>}
        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

        <div className="mb-3">
          <label htmlFor="name" className="form-label">Name:</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="biography" className="form-label">Biography:</label>
          <textarea
            className="form-control"
            id="biography"
            value={biography}
            onChange={(e) => setBiography(e.target.value)}
            rows="5"
            required
          ></textarea>
        </div>

        <button type="submit" className="btn btn-primary">Add Author</button>
      </form>
    </div>
  );
};

export default AddAuthorForm;
