import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import ManageBooks from './BooksPage';
import ManageAuthors from './AuthorsPage';
import ManageGenres from './GenresPage';
import ManageMembers from './ManageMembers';
import FooterInside from './Footer_inside';
import Navbar_dashboard from './Navbar_dashboard';
import { BsCardChecklist, BsPerson, BsBook, BsGrid } from 'react-icons/bs'; // Import necessary icons
import './styles_dashboard.css'; // Import your CSS file for dashboard styles
import bookImage from './images/library_cycle.jpg'; // Import your default book image

const Dashboard = () => {
  const [selectedNavItem, setSelectedNavItem] = useState('dashboard');
  const [books, setBooks] = useState([]);
  const [authors, setAuthors] = useState([]);
  const [genres, setGenres] = useState([]);
  const [members, setMembers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredBooks, setFilteredBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
    fetchAuthors();
    fetchGenres();
    fetchMembers();
  }, []);

  useEffect(() => {
    // Filter books based on search term
    if (searchTerm.trim() === '') {
      setFilteredBooks(books);
    } else {
      const filtered = books.filter(book =>
        book.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredBooks(filtered);
    }
  }, [searchTerm, books]);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books');
      setBooks(response.data);
      setFilteredBooks(response.data); // Initialize filtered books with all books
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchAuthors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/authors');
      setAuthors(response.data);
    } catch (error) {
      console.error('Error fetching authors:', error);
    }
  };

  const fetchGenres = async () => {
    try {
      const response = await axios.get('http://localhost:5000/genres');
      setGenres(response.data);
    } catch (error) {
      console.error('Error fetching genres:', error);
    }
  };

  const fetchMembers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/members');
      setMembers(response.data);
    } catch (error) {
      console.error('Error fetching members:', error);
    }
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleDeleteBook = async (bookId) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      try {
        await axios.delete(`http://localhost:5000/books/${bookId}`);
        setBooks(books.filter(book => book.book_id !== bookId));
        setFilteredBooks(filteredBooks.filter(book => book.book_id !== bookId));
      } catch (error) {
        console.error('Error deleting book:', error);
      }
    }
  };

  const renderBookCards = () => {
    return (
      <div className="row mt-3">
        {filteredBooks.map(book => (
          <div key={book.book_id} className="col-md-3 mb-4">
            <div className="card">
              <img src={`http://localhost:5000/images/${book.image}`} className="card-img-top" alt={book.title} />
              <div className="card-body">
                <h5 className="card-title">{book.title}</h5>
                <div className="d-flex justify-content-between align-items-center">
                  <Link to={`/books/${book.book_id}/edit`} className="btn btn-sm btn-outline-primary me-2">
                    Edit
                  </Link>
                  <button onClick={() => handleDeleteBook(book.book_id)} className="btn btn-sm btn-outline-danger">
                    Delete
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  const renderSelectedContent = () => {
    switch (selectedNavItem) {
      case 'dashboard':
        return (
          <div className="dashboard-content">
