                <div className="card text-white bg-info mb-3">
                  <div className="card-body">
                    <h5 className="card-title">Total Members</h5>
                    <div className="d-flex align-items-center">
                      <BsCardChecklist className="display-4 me-3" />
                      <h2>{members.length}</h2>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="mt-5">
              <h2>Search Books</h2>
              <input
                type="text"
                className="form-control mb-3"
                placeholder="Search books..."
                value={searchTerm}
                onChange={handleSearch}
              />
              {renderBookCards()}
            </div>
          </div>
        );
      case 'manage_books':
        return <ManageBooks />;
      case 'manage_authors':
        return <ManageAuthors />;
      case 'manage_genres':
        return <ManageGenres />;
      case 'manage_members':
        return <ManageMembers />;
      default:
