import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const EditGenrePage = () => {
  const { id } = useParams();
  const [genreName, setGenreName] = useState('');

  useEffect(() => {
    fetchGenre();
  }, []);

  const fetchGenre = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/genres/${id}`);
      // Assuming your API response is an object with a 'genre_name' field
      setGenreName(response.data.genre_name);
    } catch (error) {
      console.error('Error fetching genre:', error);
    }
  };

  const handleEditGenre = async () => {
    try {
      await axios.put(`http://localhost:5000/genres/${id}`, { genre_name: genreName });
      // Optionally, navigate back to GenresPage or handle success message
    } catch (error) {
      console.error('Error editing genre:', error);
    }
  };

  return (
    <div>
      <h2>Edit Genre</h2>
      <input
        type="text"
        value={genreName}
        onChange={(e) => setGenreName(e.target.value)}
        placeholder="Enter genre name"
      />
      <button onClick={handleEditGenre} className="btn btn-primary">Update Genre</button>
    </div>
  );
};

export default EditGenrePage;
