import React, { useEffect, useState } from 'react';
import axios from 'axios';

function FileUpload() {
  const [file, setFile] = useState(null);
  const [bookData, setBookData] = useState(null); // State to store book data
  const [bookId, setBookId] = useState(1); // Assuming book_id 1 for testing

  useEffect(() => {
    // Fetch book data initially
    axios.get(`http://localhost:5000/books/${bookId}`)
      .then(res => setBookData(res.data))
      .catch(err => console.error(err));
  }, [bookId]);

  const handleFile = (e) => {
    setFile(e.target.files[0]);
  }

  const handleUpload = () => {
    if (!file) {
      alert('Please select a file.');
      return;
    }

    const formData = new FormData();
    formData.append('image', file);
    formData.append('book_id', bookId); // Pass book_id along with the image

    axios.post('http://localhost:5000/upload', formData)
      .then(res => {
        if (res.data.Status === "Success") {
          console.log("Upload succeeded");
          // After successful upload, update the bookData to refresh image display
          axios.get(`http://localhost:5000/books/${bookId}`)
            .then(res => setBookData(res.data))
            .catch(err => console.error(err));
        } else {
          console.log("Upload failed");
        }
      })
      .catch(err => console.error(err));
  };

  return (
    <div className='container'>
        <div className="mb-3">
              <label htmlFor="title" className="form-label">Title:</label>
              <input type="text" id="title" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>
      <input type="file" onChange={handleFile} />
      <button onClick={handleUpload}>Upload</button>
      <br />
      {bookData && <img src={`http://localhost:5000/images/${bookData.image}`} alt="" />}
    </div>
  );
}

export default FileUpload;
