import React, { useEffect, useState } from 'react';
import axios from 'axios';
import uploadImage from './images/upload_image.png';
import { Link } from 'react-router-dom';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [previewImage, setPreviewImage] = useState(null); // State to store preview image URL
  const [bookData, setBookData] = useState(null); // State to store book data
  const [bookId, setBookId] = useState(null); // State to store selected book_id
  const [bookTitles, setBookTitles] = useState([]); // State to store list of book titles

  // Fetch book titles initially
  useEffect(() => {
    axios.get('http://localhost:5000/books/titles')
      .then(res => {
        setBookTitles(res.data); // Assuming res.data is an array of book titles
        if (res.data.length > 0) {
          setBookId(res.data[0].book_id); // Set default book_id based on first title
        }
      })
      .catch(err => console.error(err));
  }, []);

  // Fetch book data based on selected bookId
  useEffect(() => {
    if (bookId) {
      axios.get(`http://localhost:5000/books/${bookId}`)
        .then(res => setBookData(res.data))
        .catch(err => console.error(err));
    }
  }, [bookId]);

  // Function to handle file selection
  const handleFile = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    // Preview image before upload
    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewImage(reader.result); // Update preview image URL
      };
      reader.readAsDataURL(selectedFile);
    } else {
      setPreviewImage(null);
    }
  };

  // Function to handle image upload
  const handleUpload = () => {
    if (!file) {
      alert('Please select a file.');
      return;
    }

    const formData = new FormData();
    formData.append('image', file);
    formData.append('book_id', bookId);

    axios.post('http://localhost:5000/upload', formData)
      .then(res => {
        if (res.data.Status === "Success") {
          console.log("Upload succeeded");
          // After successful upload, refresh bookData based on selected bookId
          axios.get(`http://localhost:5000/books/${bookId}`)
            .then(res => setBookData(res.data))
            .catch(err => console.error(err));
        } else {
          console.log("Upload failed");
        }
      })
      .catch(err => console.error(err));
  };

  // Function to handle book selection
  const handleBookChange = (e) => {
    const selectedBookId = e.target.value;
    setBookId(selectedBookId);
  };

  return (
    <div
      className="add-book-container"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/images/background.jpg)`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '20px',
      }}
    >
      <div className="card shadow add-book-card" style={{ maxWidth: '800px', width: '100%' }}>
        <div className="card-body">
          <div className="row align-items-center">
            <div className="col-md-4 text-center">
              {/* Display default upload image */}
              <img src={uploadImage} alt="Upload" className="img-fluid rounded-start" style={{ maxHeight: '200px', width: 'auto' }} />
              {/* Display preview image if available */}
              {previewImage && (
                <img src={previewImage} alt="Preview" className="img-fluid mt-3" style={{ maxHeight: '200px', width: 'auto' }} />
              )}
            </div>
            <div className="col-md-8">
              <h5 className="card-title text-center mb-4">Upload Image</h5>
              <form>
                <div className="mb-3">
                  <label htmlFor="bookTitle" className="form-label">Select Book Title:</label>
                  <select id="bookTitle" className="form-select" value={bookId} onChange={handleBookChange}>
                    {bookTitles.map(book => (
                      <option key={book.book_id} value={book.book_id}>{book.title}</option>
                    ))}
                  </select>
                </div>

                <div className="mb-3">
                  <input type="file" onChange={handleFile} className="form-control" />
                </div>

                <div className="text-center mb-3">
                  <button onClick={handleUpload} className="btn btn-primary">Upload</button>
                </div>

                {bookData && (
                  <div className="text-center">
                    <img src={`http://localhost:5000/images/${bookData.image}`} alt="" className="img-fluid" />
                  </div>
                )}

                <div className="mt-3 text-center">
                  {/* Back to Dashboard Link */}
                  <Link to="/dashboard" className="btn btn-outline-primary me-2">Back to Dashboard</Link>
                  {/* Back Button */}
                  <button onClick={() => window.history.back()} className="btn btn-outline-secondary">Back</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FileUpload;
