import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Returnbook = () => {
  const [issuedBooks, setIssuedBooks] = useState([]);
  const [members, setMembers] = useState([]);
  const [selectedMember, setSelectedMember] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch list of members
    const fetchMembers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/members');
        setMembers(response.data); // Assuming response.data is an array of members
        setError(null); // Clear any previous errors
      } catch (error) {
        console.error('Error fetching members:', error);
        setError('Error fetching members');
      }
    };

    fetchMembers(); // Call fetchMembers on component mount
  }, []);

  // Fetch issued books for the selected member
  const fetchIssuedBooks = async () => {
    try {
      if (selectedMember) {
        const response = await axios.get(`http://localhost:5000/books/issued?member_id=${selectedMember}`);
        setIssuedBooks(response.data); // Assuming response.data is an array of issued books for the selected member
        setError(null); // Clear any previous errors
      } else {
        setIssuedBooks([]); // Reset issuedBooks state to an empty array if no member is selected
      }
    } catch (error) {
      console.error('Error fetching issued books:', error);
      setError('Error fetching issued books');
    }
  };

  useEffect(() => {
    fetchIssuedBooks(); // Call fetchIssuedBooks whenever selectedMember changes
  }, [selectedMember]);

  const handleReturnBook = async (bookId) => {
    if (window.confirm('Are you sure you want to return this book?')) {
      try {
        await axios.put(`http://localhost:5000/books/return/${bookId}`);
        // Remove the returned book from the issuedBooks state
        setIssuedBooks(issuedBooks.filter(book => book.book_id !== bookId));
      } catch (error) {
        console.error('Error returning book:', error);
        setError('Error returning book');
      }
    }
  };

  return (
    <div>
      <h2>Return Book</h2>
      {error && <div className="alert alert-danger">{error}</div>}

      {/* Dropdown to select members */}
      <div className="mb-3">
        <label htmlFor="memberSelect" className="form-label">Select Member:</label>
        <select
          id="memberSelect"
          className="form-select"
          value={selectedMember}
          onChange={(e) => {
            setSelectedMember(e.target.value);
          }}
        >
          <option value="">Select a member...</option>
          {members.map(member => (
            <option key={member.member_id} value={member.member_id}>{member.name}</option>
          ))}
        </select>
      </div>

      {/* Table to display issued books */}
      <table className="table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Issued To</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {issuedBooks.length > 0 ? (
            issuedBooks.map(book => (
              <tr key={book.issue_id}>
                <td>{book.title}</td>
                <td>{book.member_name}</td>
                <td>
                  <button onClick={() => handleReturnBook(book.book_id)} className="btn btn-sm btn-outline-danger">
                    Return
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">{selectedMember ? 'No books currently issued to this member.' : 'Select a member to view issued books.'}</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Returnbook;
