import React, { useState } from 'react';
import axios from 'axios';
import bookImage from './images/bookimage.png';

const AddBookForm = () => {
  const [title, setTitle] = useState('');
  const [authorId, setAuthorId] = useState('');
  const [genreId, setGenreId] = useState('');
  const [price, setPrice] = useState('');
  const [publicationDate, setPublicationDate] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/books', {
        title,
        author_id: parseInt(authorId),  // Ensure authorId is parsed as integer
        genre_id: parseInt(genreId),    // Ensure genreId is parsed as integer
        price,
        publication_date: publicationDate
      });
      console.log('Book added:', response.data);
      // Clear form fields or redirect to books page
      setTitle('');
      setAuthorId('');
      setGenreId('');
      setPrice('');
      setPublicationDate('');
    } catch (error) {
      console.error('Error adding book:', error);
    } 
  };

  return (
    <div className="container mt-5">
      <h2>Add New Book</h2>
      <div className="row">
        <div className="col-md-4">
          <img src={bookImage} alt="Book" className="img-fluid" />
        </div>
        <div className="col-md-8">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">Title:</label>
              <input
                type="text"
                className="form-control"
                id="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="authorId" className="form-label">Author ID:</label>
              <input
                type="text"
                className="form-control"
                id="authorId"
                value={authorId}
                onChange={(e) => setAuthorId(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="genreId" className="form-label">Genre ID:</label>
              <input
                type="text"
                className="form-control"
                id="genreId"
                value={genreId}
                onChange={(e) => setGenreId(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="price" className="form-label">Price:</label>
              <input
                type="text"
                className="form-control"
                id="price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="publicationDate" className="form-label">Publication Date:</label>
              <input
                type="text"
                className="form-control"
                id="publicationDate"
                value={publicationDate}
                onChange={(e) => setPublicationDate(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Add Book</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddBookForm;
