import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const EditBookId = () => {
  const { book_id } = useParams();

  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);

  const [title, setTitle] = useState('');
  const [authorName, setAuthorName] = useState('');
  const [genreName, setGenreName] = useState('');
  const [price, setPrice] = useState('');
  const [publicationDate, setPublicationDate] = useState('');

  const [authors, setAuthors] = useState([]);
  const [genres, setGenres] = useState([]);

  useEffect(() => {
    fetchAuthors();
    fetchGenres();
  }, []);

  useEffect(() => {
    if (book_id) {
      fetchBookDetails();
    }
  }, [book_id]);

  const fetchAuthors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/authors');
      setAuthors(response.data);
    } catch (error) {
      console.error('Error fetching authors:', error);
    }
  };

  const fetchGenres = async () => {
    try {
      const response = await axios.get('http://localhost:5000/genres');
      setGenres(response.data);
    } catch (error) {
      console.error('Error fetching genres:', error);
    }
  };

  const fetchBookDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/books/${book_id}`);
      const { title, author_name, genre_name, price, publication_date } = response.data;
      setSelectedBook(response.data);
      setTitle(title);
      setAuthorName(author_name);
      setGenreName(genre_name);
      setPrice(price);
      setPublicationDate(new Date(publication_date).toISOString().slice(0, 10));
    } catch (error) {
      console.error('Error fetching book details:', error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5000/books/${book_id}`, {
        title,
        author_name: authorName,
        genre_name: genreName,
        price,
        publication_date: new Date(publicationDate).toISOString().slice(0, 10),
      });
      console.log('Book updated:', response.data);
      alert('Book updated successfully!');
    } catch (error) {
      console.error('Error updating book:', error);
    }
  };

  const handleSearch = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.get(`http://localhost:5000/books?title=${searchTerm}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching books:', error);
    }
  };

  const handleSelectBook = (book) => {
    setSelectedBook(book);
    setTitle(book.title);
    setAuthorName(book.author_name);
    setGenreName(book.genre_name);
    setPrice(book.price);
    setPublicationDate(new Date(book.publication_date).toISOString().slice(0, 10));
  };

  return (
    <div className="container">
      <div className="card shadow">
        <div className="card-body">
          <h5 className="card-title text-center mb-4">Edit Book</h5>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="searchTerm" className="form-label">Search Book by Title:</label>
              <div className="input-group">
                <input
                  type="text"
                  id="searchTerm"
                  className="form-control"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button className="btn btn-primary" type="button" onClick={handleSearch}>Search</button>
              </div>
            </div>

            {searchResults.length > 0 && (
              <div className="mb-3">
                <label htmlFor="selectBook" className="form-label">Select Book:</label>
                <select
                  id="selectBook"
                  className="form-select"
                  value={selectedBook ? selectedBook.book_id : ''}
                  onChange={(e) => handleSelectBook(searchResults.find(book => book.book_id === parseInt(e.target.value)))}
                  required
                >
                  <option value="">Select Book</option>
                  {searchResults.map(book => (
                    <option key={book.book_id} value={book.book_id}>{book.title}</option>
                  ))}
                </select>
              </div>
            )}

            {selectedBook && (
              <>
                <div className="mb-3">
                  <label htmlFor="title" className="form-label">Title:</label>
                  <input type="text" id="title" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} required />
                </div>

                <div className="mb-3">
                  <label htmlFor="authorName" className="form-label">Author Name:</label>
                  <select
                    id="authorName"
                    className="form-select"
                    value={authorName}
                    onChange={(e) => setAuthorName(e.target.value)}
                    required
                  >
                    <option value="">Select Author</option>
                    {authors.map((author) => (
                      <option key={author.author_id} value={author.name}>{author.name}</option>
                    ))}
                  </select>
                </div>

                <div className="mb-3">
                  <label htmlFor="genreName" className="form-label">Genre Name:</label>
                  <select
                    id="genreName"
                    className="form-select"
                    value={genreName}
                    onChange={(e) => setGenreName(e.target.value)}
                    required
                  >
                    <option value="">Select Genre</option>
                    {genres.map((genre) => (
                      <option key={genre.genre_id} value={genre.genre_name}>{genre.genre_name}</option>
                    ))}
                  </select>
                </div>

                <div className="mb-3">
                  <label htmlFor="price" className="form-label">Price:</label>
                  <input type="text" id="price" className="form-control" value={price} onChange={(e) => setPrice(e.target.value)} required />
                </div>

                <div className="mb-3">
                  <label htmlFor="publicationDate" className="form-label">Publication Date:</label>
                  <input
                    type="date"
                    id="publicationDate"
                    className="form-control"
                    value={publicationDate}
                    onChange={(e) => setPublicationDate(e.target.value)}
                    required
                  />
                </div>

                <div className="text-center">
                  <button type="submit" className="btn btn-primary">Update Book</button>
                </div>
              </>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditBookId;
