import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Returnbook = () => {
  const [issuedBooks, setIssuedBooks] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchIssuedBooks();
  }, []);

  const fetchIssuedBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/issued_books');
      const enhancedBooks = await Promise.all(response.data.map(async (book) => {
        const bookDetails = await axios.get(`http://localhost:5000/books/${book.book_id}`);
        const memberDetails = await axios.get(`http://localhost:5000/members/${book.member_id}`);
        return {
          ...book,
          title: bookDetails.data.title,
          member_name: memberDetails.data.name
        };
      }));
      setIssuedBooks(enhancedBooks);
      setError(null); // Clear any previous errors
    } catch (error) {
      console.error('Error fetching issued books:', error);
      setError('Error fetching issued books: ' + error.message);
      setIssuedBooks([]); // Reset issuedBooks state to an empty array on error
    }
  };

  const handleReturnBook = async (bookId) => {
    if (window.confirm('Are you sure you want to return this book?')) {
      try {
        await axios.put(`http://localhost:5000/books/return/${bookId}`);
        // Remove the returned book from the issuedBooks state
        setIssuedBooks(issuedBooks.filter(book => book.book_id !== bookId));
      } catch (error) {
        console.error('Error returning book:', error);
        setError('Error returning book');
      }
    }
  };

  return (
    <div>
      <h2>Return Book</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <table className="table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Issued To</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
  {issuedBooks.length > 0 ? (
    issuedBooks.map(book => (
      <tr key={book.issue_id}>
        <td>{book.title}</td>
        <td>{book.member_name}</td>
        <td>
          <button onClick={() => handleReturnBook(book.book_id)} className="btn btn-sm btn-outline-danger">
            Return
          </button>
        </td>
      </tr>
    ))
  ) : (
    <tr>
      <td colSpan="3">{error ? error : 'No books currently issued.'}</td>
    </tr>
  )}
</tbody>
      </table>
    </div>
  );
};

export default Returnbook;
