import React, { useState } from 'react';
import Navbar from './Navbar';
import Carousel from './Carousel';
import './styles.css'; // Import your custom styles

const HomePage = () => {
  const [showAbout, setShowAbout] = useState(false);

  const handleAboutClick = () => {
    setShowAbout(true);
  };

  return (
    <div>
      <Navbar handleAboutClick={handleAboutClick} />
      <div className="container mt-4">
        <Carousel />
        {showAbout && (
          <div className="mt-4">
            <h2>Welcome to Our Online Library!</h2>
            <p>At Book Hub<b></b>, we are dedicated to providing you with a seamless and enriching reading experience. Whether you're a student, an academic researcher, or simply an avid reader, our online library offers a vast collection of books across various genres and disciplines.</p>

            <h3>Our Mission</h3>
            <p>Our mission is to make knowledge accessible to everyone, anytime, anywhere. Through our digital platform, we aim to break down barriers to information and foster a love for reading and learning.</p>

            <h3>What We Offer</h3>
            <ul>
              <li><strong>Extensive Collection:</strong> Explore thousands of books, from classic literature to contemporary bestsellers.</li>
              <li><strong>User-Friendly Interface:</strong> Navigate our website effortlessly with intuitive search and browsing features.</li>
              <li><strong>Personalization:</strong> Create your own reading lists and receive recommendations tailored to your interests.</li>
              <li><strong>Accessibility:</strong> Enjoy 24/7 access to our library from any device, ensuring you never have to wait to dive into a good book.</li>
            </ul>

            <h3>Why Choose Us?</h3>
            <ul>
              <li><strong>Quality Selection:</strong> Each book in our collection is carefully curated to ensure relevance and value.</li>
              <li><strong>Community Engagement:</strong> Join discussions, book clubs, and events that bring readers together.</li>
              <li><strong>Educational Resources:</strong> Access supplementary materials and resources to enhance your learning experience.</li>
            </ul>

            <h3>Get Started Today</h3>
            <p>Join our community of readers and explore the world of literature like never before. Whether you're here to study, unwind, or expand your horizons, [Library Name] is your gateway to a world of knowledge.</p>
          </div>
        )}
      </div>
      {/* Additional content for your home page can be added here */}
    </div>
  );
};

export default HomePage;
