import React, { useState, useEffect } from 'react';
import './styles.css'; // Import your custom styles
import { Link } from 'react-router-dom';

const Carousel = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const images = [

    '/images/C_adult.jpg',
    '/images/C_fiction.jpg',
    '/images/C_kids.jpg',
    '/images/C_musical.jpg',

    // Add more image paths as needed
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
    }, 5000); // Change image every 5 seconds (5000 milliseconds)

    return () => clearInterval(interval);
  }, []);

  return (
    <div id="carouselExampleSlidesOnly" className="carousel slide" data-ride="carousel">
      <div className="carousel-inner">
        {images.map((image, index) => (
          <div key={index} className={`carousel-item ${index === currentImageIndex ? 'active' : ''}`}>
            <img src={process.env.PUBLIC_URL + image} className="d-block w-100" alt={`Slide ${index}`} />
          </div>
        ))}
      </div>
      <div className="carousel-caption d-flex flex-column justify-content-center align-items-center">
        <h5 className="text-center">Library Management</h5>
        <p className="text-center">Effectively manage, track, and perform daily library operations with ease!</p>
        <div className="d-grid gap-2 d-md-flex justify-content-md-center">
          <Link to="/AdminLogin" className="btn btn-primary me-md-2">Login as Admin</Link>

        </div>
      </div>
    </div>
  );
};

export default Carousel;
