import React, { useEffect, useState } from 'react';
import axios from 'axios';
import uploadImage from './images/upload_image.png';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [bookData, setBookData] = useState(null); // State to store book data
  const [bookId, setBookId] = useState(null); // State to store selected book_id
  const [bookTitles, setBookTitles] = useState([]); // State to store list of book titles

  // Fetch book titles initially
  useEffect(() => {
    axios.get('http://localhost:5000/books/titles')
      .then(res => {
        setBookTitles(res.data); // Assuming res.data is an array of book titles
        if (res.data.length > 0) {
          setBookId(res.data[0].book_id); // Set default book_id based on first title
        }
      })
      .catch(err => console.error(err));
  }, []);

  // Fetch book data based on selected bookId
  useEffect(() => {
    if (bookId) {
      axios.get(`http://localhost:5000/books/${bookId}`)
        .then(res => setBookData(res.data))
        .catch(err => console.error(err));
    }
  }, [bookId]);

  const handleFile = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = () => {
    if (!file) {
      alert('Please select a file.');
      return;
    }

    const formData = new FormData();
    formData.append('image', file);
    formData.append('book_id', bookId);

    axios.post('http://localhost:5000/upload', formData)
      .then(res => {
        if (res.data.Status === "Success") {
          console.log("Upload succeeded");
          // After successful upload, refresh bookData based on selected bookId
          axios.get(`http://localhost:5000/books/${bookId}`)
            .then(res => setBookData(res.data))
            .catch(err => console.error(err));
        } else {
          console.log("Upload failed");
        }
      })
      .catch(err => console.error(err));
  };

  const handleBookChange = (e) => {
    const selectedBookId = e.target.value;
    setBookId(selectedBookId);
  };

  return (
    <div
      className="add-book-container"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/images/background.jpg)`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '20px',
      }}
    >
      <div className="container">
        <div className="card mb-3" style={{ maxWidth: '400px' }}>
          <div className="row g-0">
            <div className="col-md-4">
              <img src={uploadImage} alt="Upload" className="img-fluid rounded-start" />
            </div>
            <div className="col-md-8">
              <div className="card-body">
                <h5 className="card-title">Upload Image</h5>
                <div className="mb-3">
                  <label htmlFor="bookTitle" className="form-label">Select Book Title:</label>
                  <select id="bookTitle" className="form-select" value={bookId} onChange={handleBookChange}>
                    {bookTitles.map(book => (
                      <option key={book.book_id} value={book.book_id}>{book.title}</option>
                    ))}
                  </select>
                </div>

                <input type="file" onChange={handleFile} className="form-control mb-3" />
                <button onClick={handleUpload} className="btn btn-primary mb-3">Upload</button>

                {bookData && (
                  <div>
                    <img src={`http://localhost:5000/images/${bookData.image}`} alt="" className="img-fluid" />
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FileUpload;
