import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ManageBooks from './BooksPage';
import ManageAuthors from './AuthorsPage';
import ManageGenres from './GenresPage';
import ManageMembers from './ManageMembers'; 
import CarouselCom from './CarouselComponent';

import FooterInside from './Footer_inside';
import Navbar_dashboard from './Navbar_dashboard';

import './styles_dashboard.css'; // Import your CSS file for dashboard styles

import discoverImage from '../components/images/cd_image1.jpg'; 
import libLogo from '../components/images/cd_.png';
import libcyc from '../components/images/lib_cycle.png';
import hLibPic from '../components/images/h_libpic.avif';


const Dashboard = () => {
  const [selectedNavItem, setSelectedNavItem] = useState('dashboard');

  const renderSelectedContent = () => {
    switch (selectedNavItem) {
      case 'dashboard':
        return (
          <div>
            <p>Welcome to the Admin Dashboard of Book Kingdom.</p>
            img src={process.env.PUBLIC_URL + discoverImage} alt="Discover" className="w-100 mb-4" />
        <CarouselCom topImage={discoverImage}/>
          </div>
        );
      case 'manage_books':
        return <ManageBooks />;
      case 'manage_authors':
        return <ManageAuthors />;
      case 'manage_genres':
        return <ManageGenres />;
      case 'manage_members':
        return <ManageMembers />;
      default:
        return null;
    }
  };

  return (
    <div className="container-fluid">
      <Navbar_dashboard />

      <div className="row">
        {/* Sidebar */}
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav nav-pills flex-column">
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'dashboard' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('dashboard')}
                >
                  Dashboard
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_books' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_books')}
                >
                  Book Inventory
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_authors' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_authors')}
                >
                  Manage Authors
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_genres' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_genres')}
                >
                  Manage Genres
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_members' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_members')}
                >
                  Manage Members
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        {/* Page content */}
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="pt-3 pb-2 mb-3">
            {renderSelectedContent()}
          </div>
        </main>
      </div>

      <FooterInside />
    </div>
  );
};

export default Dashboard;
