import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const GenresPage = () => {
  const [genres, setGenres] = useState([]);

  useEffect(() => {
    fetchGenres();
  }, []);

  const fetchGenres = async () => {
    try {
      const response = await axios.get('http://localhost:5000/genres');
      setGenres(response.data);
    } catch (error) {
      console.error('Error fetching genres:', error);
    }
  };


  const handleDeleteGenre = async (genreId) => {
    if (window.confirm('Are you sure you want to delete this genre?')) {
      try {
        await axios.delete(`http://localhost:5000/genres/${genreId}`);
        setGenres(genres.filter(genre => genre.genre_id !== genreId));
      } catch (error) {
        console.error('Error deleting genre:', error);
      }
    }
  };

  return (
    <div>
      <h2>All Genres</h2>
      <div style={{ marginBottom: '20px' }}>
        <Link to="/genres/add" className="btn btn-primary">Add Genre</Link>
      </div>
      <ul>
        {genres.map(genre => (
          <li key={genre.genre_id}>
            {genre.genre_name}
            {' '}
            <Link to={`/genres/edit/${genre.genre_id}`} className="btn btn-sm btn-outline-primary">Edit</Link>
            {' '}
            <button
              className="btn btn-sm btn-outline-danger"
              onClick={() => handleDeleteGenre(genre.genre_id)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default GenresPage;
