import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ManageBooks from './BooksPage';
import ManageAuthors from './AuthorsPage';
import ManageGenres from './GenresPage';
import ManageMembers from './ManageMembers';
import FooterInside from './Footer_inside';
import Navbar_dashboard from './Navbar_dashboard';
import { ProSidebar, Menu, MenuItem } from 'react-pro-sidebar'; // Import necessary components
import 'react-pro-sidebar/dist/scss/styles.scss'; // Import SCSS instead of CSS

const Dashboard = () => {
  const [selectedNavItem, setSelectedNavItem] = useState('dashboard');

  const handleItemClick = (itemName) => {
    setSelectedNavItem(itemName);
  };

  const renderSelectedContent = () => {
    switch (selectedNavItem) {
      case 'dashboard':
        return (
          <div>
            <h2>Dashboard</h2>
            <p>Welcome to the Admin Dashboard of BookHub.</p>
          </div>
        );
      case 'manage_books':
        return <ManageBooks />;
      case 'manage_authors':
        return <ManageAuthors />;
      case 'manage_genres':
        return <ManageGenres />;
      case 'manage_members': // New case for managing members
        return <ManageMembers />;
      default:
        return null;
    }
  };

  return (
    <div className="container-fluid">
      <Navbar_dashboard />

      <div className="row">
        <ProSidebar className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <Menu iconShape="square">
            <MenuItem
              onClick={() => handleItemClick('dashboard')}
              active={selectedNavItem === 'dashboard'}
            >
              Dashboard
            </MenuItem>
            <MenuItem
              onClick={() => handleItemClick('manage_books')}
              active={selectedNavItem === 'manage_books'}
            >
              Book Inventory
            </MenuItem>
            <MenuItem
              onClick={() => handleItemClick('manage_authors')}
              active={selectedNavItem === 'manage_authors'}
            >
              Manage Authors
            </MenuItem>
            <MenuItem
              onClick={() => handleItemClick('manage_genres')}
              active={selectedNavItem === 'manage_genres'}
            >
              Manage Genres
            </MenuItem>
            <MenuItem
              onClick={() => handleItemClick('manage_members')}
              active={selectedNavItem === 'manage_members'}
            >
              Manage Members
            </MenuItem>
          </Menu>
        </ProSidebar>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="pt-3 pb-2 mb-3">{renderSelectedContent()}</div>
        </main>
      </div>

      <FooterInside />
    </div>
  );
};

export default Dashboard;
