import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import bookImage from './images/bookimage.png';

const AddBookForm = () => {
  const [title, setTitle] = useState('');
  const [authorName, setAuthorName] = useState('');
  const [genreName, setGenreName] = useState('');
  const [price, setPrice] = useState('');
  const [publicationDate, setPublicationDate] = useState(new Date());
  const [authors, setAuthors] = useState([]);
  const [genres, setGenres] = useState([]);
  const [imageFile, setImageFile] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchAuthors = async () => {
      try {
        const response = await axios.get('http://localhost:5000/authors');
        setAuthors(response.data);
      } catch (error) {
        console.error('Error fetching authors:', error);
      }
    };

    const fetchGenres = async () => {
      try {
        const response = await axios.get('http://localhost:5000/genres');
        setGenres(response.data);
      } catch (error) {
        console.error('Error fetching genres:', error);
      }
    };

    fetchAuthors();
    fetchGenres();
  }, []);

  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const formData = new FormData();
      formData.append('image', imageFile); // Append the image file to the form data
      formData.append('title', title);
      formData.append('author_name', authorName);
      formData.append('genre_name', genreName);
      formData.append('price', price);
      formData.append('publication_date', publicationDate.toISOString().split('T')[0]);

      const response = await axios.post('http://localhost:5000/books', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Book added:', response.data);
      setMessage('Book added successfully!');
      setTitle('');
      setAuthorName('');
      setGenreName('');
      setPrice('');
      setPublicationDate(new Date());
      setImageFile(null); // Reset the image file state
    } catch (error) {
      console.error('Error adding book:', error);
      if (error.response) {
        console.error('Error response from server:', error.response.data);
      }
      setMessage('Error adding book. Please try again.');
    }
  };


  return (
    <div className="container">
      <div className="card shadow">
        <div className="card-body">
          <div className="text-center mb-4">
            {imageFile ? (
              <img src={URL.createObjectURL(imageFile)} alt="Book" className="book-image" style={{ maxWidth: '200px', marginBottom: '10px' }} />
            ) : (
              <img src={bookImage} alt="Book" className="book-image" />
            )}
          </div>
          <h5 className="card-title text-center mb-4">Add New Book</h5>
          {message && <div className={`alert ${message.includes('Error') ? 'alert-danger' : 'alert-success'}`} role="alert">
            {message}
          </div>}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">
                Title:
              </label>
              <input type="text" id="title" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>

            <div className="mb-3">
              <label htmlFor="authorName" className="form-label">
                Author Name:
              </label>
              <select
                id="authorName"
                className="form-select"
                value={authorName}
                onChange={(e) => setAuthorName(e.target.value)}
                required
              >
                <option value="">Select Author</option>
                {authors.map((author) => (
                  <option key={author.author_id} value={author.name}>
                    {author.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-3">
              <label htmlFor="genreName" className="form-label">
                Genre Name:
              </label>
              <select
                id="genreName"
                className="form-select"
                value={genreName}
                onChange={(e) => setGenreName(e.target.value)}
                required
              >
                <option value="">Select Genre</option>
                {genres.map((genre) => (
                  <option key={genre.genre_id} value={genre.genre_name}>
                    {genre.genre_name}
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-3">
              <label htmlFor="price" className="form-label">
                Price:
              </label>
              <input type="text" id="price" className="form-control" value={price} onChange={(e) => setPrice(e.target.value)} required />
            </div>

            <div className="mb-3">
              <label htmlFor="publicationDate" className="form-label">
                Publication Date:
              </label>
              <br />
              <DatePicker
                id="publicationDate"
                selected={publicationDate}
                onChange={(date) => setPublicationDate(date)}
                className="form-control"
                dateFormat="yyyy-MM-dd"
                required
              />
            </div>

            <div className="mb-3">
              <label htmlFor="image" className="form-label">
                Book Image:
              </label>
              <input type="file" id="image" className="form-control" onChange={handleImageChange} accept="image/*" required />
            </div>

            <div className="text-center">
              <button type="submit" className="btn btn-primary">
                Add Book
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddBookForm;
