import React, { useState, useEffect } from 'react';
import axios from 'axios';
import bookImage from './images/bookimage.png'; // Import book image
import './css/AddBookForm.css'; // Import custom CSS for styling

const AddBookForm = () => {
  const [title, setTitle] = useState('');
  const [authorName, setAuthorName] = useState('');
  const [genreName, setGenreName] = useState('');
  const [price, setPrice] = useState('');
  const [publicationDate, setPublicationDate] = useState('');
  const [authors, setAuthors] = useState([]);
  const [genres, setGenres] = useState([]);

  useEffect(() => {
    const fetchAuthors = async () => {
      try {
        const response = await axios.get('http://localhost:5000/authors');
        setAuthors(response.data);
      } catch (error) {
        console.error('Error fetching authors:', error);
      }
    };

    const fetchGenres = async () => {
      try {
        const response = await axios.get('http://localhost:5000/genres');
        setGenres(response.data);
      } catch (error) {
        console.error('Error fetching genres:', error);
      }
    };

    fetchAuthors();
    fetchGenres();
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/books', {
        title,
        author_name: authorName,
        genre_name: genreName,
        price,
        publication_date: publicationDate,
      });
      console.log('Book added:', response.data);
      // Clear form fields or redirect to books page
      setTitle('');
      setAuthorName('');
      setGenreName('');
      setPrice('');
      setPublicationDate('');
    } catch (error) {
      console.error('Error adding book:', error);
    }
  };

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-6 offset-md-3">
          <div className="card shadow">
            <div className="card-body">
              <div className="text-center mb-4">
                <img src={bookImage} alt="Book" className="book-image" />
              </div>
              <h5 className="card-title text-center mb-4">Add New Book</h5>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="title" className="form-label">Title:</label>
                  <input type="text" id="title" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} required />
                </div>

                <div className="mb-3">
                  <label htmlFor="authorName" className="form-label">Author Name:</label>
                  <select
                    id="authorName"
                    className="form-select"
                    value={authorName}
                    onChange={(e) => setAuthorName(e.target.value)}
                    required
                  >
                    <option value="">Select Author</option>
                    {authors.map((author) => (
                      <option key={author.author_id} value={author.name}>{author.name}</option>
                    ))}
                  </select>
                </div>

                <div className="mb-3">
                  <label htmlFor="genreName" className="form-label">Genre Name:</label>
                  <select
                    id="genreName"
                    className="form-select"
                    value={genreName}
                    onChange={(e) => setGenreName(e.target.value)}
                    required
                  >
                    <option value="">Select Genre</option>
                    {genres.map((genre) => (
                      <option key={genre.genre_id} value={genre.genre_name}>{genre.genre_name}</option>
                    ))}
                  </select>
                </div>

                <div className="mb-3">
                  <label htmlFor="price" className="form-label">Price:</label>
                  <input type="text" id="price" className="form-control" value={price} onChange={(e) => setPrice(e.target.value)} required />
                </div>

                <div className="mb-3">
                  <label htmlFor="publicationDate" className="form-label">Publication Date:</label>
                  <input type="text" id="publicationDate" className="form-control" value={publicationDate} onChange={(e) => setPublicationDate(e.target.value)} required />
                </div>

                <div className="text-center">
                  <button type="submit" className="btn btn-primary">Add Book</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddBookForm;
