import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { BsPlusSquare, BsPencilSquare, BsSearch, BsTrash, BsListUl, BsArrowLeft, BsArrowRight } from 'react-icons/bs';

const BooksPage = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const handleDeleteBook = async (bookId) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      try {
        await axios.delete(`http://localhost:5000/books/${bookId}`);
        // Update the books state after deletion
        setBooks(books.filter(book => book.book_id !== bookId));
      } catch (error) {
        console.error('Error deleting book:', error);
      }
    }
  };

  return (
    <div>
      <h2>Admin Library - All Books</h2>
      <div className="mb-3">
        <Link to="/books/add" className="btn btn-primary me-2">
          <BsPlusSquare className="me-2" /> Add Book
        </Link>
        <Link to="/" className="btn btn-secondary me-2">
          <BsArrowLeft className="me-2" /> Return Book
        </Link>
        <Link to="/" className="btn btn-secondary me-2">
          <BsArrowRight className="me-2" /> Issue Book
        </Link>
        <button className="btn btn-info me-2">
          <BsSearch className="me-2" /> Search Book
        </button>
        <button className="btn btn-warning me-2">
          <BsPencilSquare className="me-2" /> Edit Book
        </button>
        <button className="btn btn-danger me-2">
          <BsTrash className="me-2" /> Delete Book
        </button>
        <Link to="/" className="btn btn-dark">
          <BsListUl className="me-2" /> Show Books
        </Link>
      </div>
      <ul className="list-group">
        {books.map(book => (
          <li key={book.book_id} className="list-group-item d-flex justify-content-between align-items-center">
            <Link to={`/books/${book.book_id}`}>{book.title}</Link>
            <div>
              <Link to={`/books/${book.book_id}/edit`} className="btn btn-sm btn-outline-primary me-2">
                Edit
              </Link>
              <button onClick={() => handleDeleteBook(book.book_id)} className="btn btn-sm btn-outline-danger">
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BooksPage;
