import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ManageBooks from './BooksPage'; // Assuming this component is correctly exported from './BooksPage'
import ManageAuthors from './AuthorsPage'; // Assuming this component is correctly exported from './AuthorsPage'
import ManageGenres from './GenresPage'; // Assuming this component is correctly exported from './GenresPage'
import FooterInside from './Footer_inside'; // Assuming this component is correctly exported from './Footer_inside'

const Dashboard = () => {
  const [selectedNavItem, setSelectedNavItem] = useState('dashboard');

  const renderSelectedContent = () => {
    switch (selectedNavItem) {
      case 'dashboard':
        return (
          <div>
            <h2>Dashboard</h2>
            <p>Welcome to the Admin Dashboard of BookHub.</p>
          </div>
        );
      case 'manage_books':
        return <ManageBooks />;
      case 'manage_authors':
        return <ManageAuthors />;
      case 'manage_genres':
        return <ManageGenres />;
      default:
        return null;
    }
  };

  return (
    <div className="container-fluid">
      <nav className="navbar navbar-expand-lg navbar-dark bg-info">
        <div className="container-fluid">
          <Link className="navbar-brand d-flex align-items-center" to="/">
            {/* Adjust this based on your actual logo path */}
            <img
              src={process.env.PUBLIC_URL + '/images/booklogo.png'}
              alt="BookLogo"
              width="70"
              height="70"
              className="d-inline-block align-top me-2"
            />
            Book Hub
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="navbar-text ms-auto">Hello, Admin</div>
        </div>
      </nav>

      <div className="row">
        {/* Sidebar */}
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav flex-column">
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'dashboard' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('dashboard')}
                >
                  Dashboard
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_books' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_books')}
                >
                  Book Inventory
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_authors' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_authors')}
                >
                  Manage Authors
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'manage_genres' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('manage_genres')}
                >
                  Manage Genres
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        {/* Page content */}
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="pt-3 pb-2 mb-3">
            {renderSelectedContent()}
          </div>
        </main>
      </div>

      <FooterInside />
    </div>
  );
};

export default Dashboard;
