// Example search and pagination implementation

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ManageBooks from './BooksPage';
import ManageAuthors from './AuthorsPage';
import ManageGenres from './GenresPage';
import ManageMembers from './ManageMembers';
import FooterInside from './Footer_inside';
import Navbar_dashboard from './Navbar_dashboard';
import { Link } from 'react-router-dom';
import { BsCardChecklist, BsPerson, BsBook, BsGrid, BsArrowRightShort, BsArrowLeftShort } from 'react-icons/bs'; // Import necessary icons
import './styles_dashboard.css'; // Import your CSS file for dashboard styles
//import bookImage from './images/library_cycle.jpg'; // Import your book image

const Dashboard = () => {
  const [selectedNavItem, setSelectedNavItem] = useState('dashboard');
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [booksPerPage] = useState(5); // Number of books per page

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/books');
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  // Handle search input change
  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  // Filter books based on search term
  const filteredBooks = books.filter((book) =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Pagination logic
  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  const currentBooks = filteredBooks.slice(indexOfFirstBook, indexOfLastBook);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const renderSelectedContent = () => {
    switch (selectedNavItem) {
      case 'dashboard':
        return (
          <div className="dashboard-content">
            <h4>Welcome to the Admin Dashboard of Book Kingdom</h4>
            <p className="lead">Manage the book inventory, authors, genres, and members.</p>
            {/* Example search bar */}
            <div className="input-group mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="Search Books..."
                value={searchTerm}
                onChange={handleSearchChange}
              />
            </div>
            {/* Example pagination */}
            <ul className="pagination">
              {currentBooks.map((book) => (
                <li key={book.id} className="page-item">
                  <Link to={`/books/${book.id}`} className="page-link">
                    {book.title}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        );
      // Add cases for other nav items (manage_books, manage_authors, manage_genres, manage_members)
      default:
        return null;
    }
  };

  return (
    <div className="container-fluid">
      {/* Your existing Navbar and Sidebar */}
      <Navbar_dashboard setSelectedNavItem={setSelectedNavItem} />
      <div className="row">
        {/* Your existing Sidebar */}
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav nav-pills flex-column">
              <li className="nav-item">
                <Link
                  className={`nav-link ${selectedNavItem === 'dashboard' ? 'active' : ''}`}
                  onClick={() => setSelectedNavItem('dashboard')}
                >
                  <BsGrid className="me-2" /> Dashboard
                </Link>
              </li>
              {/* Add other nav items here */}
            </ul>
          </div>
        </nav>
        {/* Page content */}
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="pt-3 pb-2 mb-3">
            {renderSelectedContent()}
          </div>
        </main>
      </div>
      <FooterInside />
    </div>
  );
};

export default Dashboard;
